
function InfoWindow(marker){this.marker=marker;const HTML=this.html(marker.name,marker.address,marker.hours);google.maps.InfoWindow.call(this,{content:HTML,});}
InfoWindow.prototype=Object.create(google.maps.InfoWindow.prototype);InfoWindow.prototype.opened=false;InfoWindow.prototype.html=function(name,address,hours){const HTML=`<h3> ${name}</h3>`+`<p><strong>Address:</strong> ${address}</p>`+`<p><strong>Hours:</strong> ${hours}</p>`;return HTML;};InfoWindow.prototype.open=function(){google.maps.InfoWindow.prototype.open.call(this,this.marker.map,this.marker);this.opened=true;};InfoWindow.prototype.close=function(){google.maps.InfoWindow.prototype.close.call(this);this.opened=false;};